    useEffect(() => {
        setActiveShops(props.shops)
        console.log(activeShops)
    });

    
    return (
        <div className={extended}>

            <div className="shoplist-filter-container">
                <p>Filter by: </p>
                <button className={ratingFilter} onClick = {onRatingFilterClick}>Rating</button>
                <button className={distanceFilter} onClick = {onDistanceFilterClick}>Distance</button>
            </div>

            <ul className="shoplist-list">{shopNames}</ul>
            
        </div>
        
    );
}

export default Shoplist;
