import React, { useEffect, useState } from 'react';
import { calcDist, filterShops } from '../utils/utils.js';
import Shop from './Shop.js';

function Shoplist(props) {

    const [activeShops, setActiveShops] = useState([]);
    const [shoplistExtended, setShoplistExtended] = useState(false)

    console.log(props.location)
    filterShops('distance', props.shops, props.location);
    
    const shopNames = props.shops.map((shop) => 
        <li key={shop['place_id']}><Shop name={shop['name']} details ={shop}/></li>
    );

    let className = "shoplist-hidden"
    setShoplistExtended(props.extended);
    
    useEffect(() => {
        if (shoplistExtended === true) {
            className = "shoplist-extended"
        }
    }, [shoplistExtended]);

    return (
        <div className={shoplistExtended}>

            <div className="shoplist-filter-container">
                <button>Filter 1</button>
                <button>Filter 2</button>
            </div>

            <ul className="shoplist-list">{shopNames}</ul>
            
        </div>
        
    );
}

export default Shoplist;
