import React, { Component, useEffect } from 'react'
import Filter from './Filter';

export default class Sidebar extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        let className = 'sidebar-hidden'
        if (this.props.extended === true) {
            className = 'sidebar-extended';
        }

        //temp array with filters
        const filters = ["spicy", "soup", "halal", "hot", "vegetarian", "shared", "fried"]


        //make call to database to retrieve filters
        const getFilters = async () => {
            try {
                const response = await fetch("http://localhost:5000/food/1/");
                const jsonData = response.json();

                console.log(jsonData);
            } catch (err) {
                console.error(err);
            }
        };

        useEffect (() => {
            getFilters();
        })


        const filterList = filters.map((filter) =>
            <li id={filter}><Filter filter={filter}/></li>
        );

        return (
        <div className={className}>
            <img id="logo-img"src={require("../resources/images/whatoeat-logo.png")}/>
            <ul id="filter-list">
                {filterList}
            </ul>
        </div>
        )
    }
}
