import logo from './logo.svg';
import { Component } from 'react';
import './App.css';
import Map from './components/Map.js';
import Shoplist from './components/Shoplist.js';

import Sidebar from './components/Sidebar';
import Navbar from './components/Navbar';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      extended: false
    };

    this.handleMenuClick = this.handleMenuClick.bind(this);
    this.handleFilterSubmit = this.handleFilterSubmit.bind(this);
  }

  handleMenuClick() {
    this.setState({extended: !this.state.extended});
  }

  handleFilterSubmit = async() => {
    try {
        // console.log(activeFilters)
        const response = await fetch("http://localhost:5000/submit", { method:'POST', headers: {'Accept': 'application/json', 'Content-Type': 'application/json'}, body: JSON.stringify(activeFilters)});
        let resJson = response.json();
        // let foodNames = resJson.map((foodName) => console.log(foodName));
        resJson.then((data) => {
            let food_names = [];
            for (let food in data) {
                food_names.push(data[food].food_name);
            }
            setPossibleFoodItems(food_names);
        });
    } catch (err) {
        console.log(err.message)
    }

  }

  render() {
    console.log("app rendered")
    const shops = ["moshi store", "good food", "best food", "shit store", "another store", "food store", "running out of names"];
    return (
      <body className="body-container">
        {/* <div>
          <Map food={this.state.food != ''? this.state.food : this.state.placeholder}/>
        </div> */}
        <div className="sidebar-navbar">
          <div className = "sidebar-container">
            <Sidebar extended={this.state.extended} handleFilterSubmit={handleFilterSubmit}/>
          </div>
          <Navbar onClick={this.handleMenuClick}/>
        </div>

        <Shoplist shops={shops} extended={this.state.extended}/>


      </body>
    );
  }
}

export default App;
